import React , {useState, useEffect} from 'react'
import {FaHourglass} from 'react-icons/fa'


function Timer() {
    function beep() {
        var snd = new Audio("data:audio/wav;base64,");  
        snd.play();
    }

    const [count, setCount] =useState(Number(6))
    const [running, setRunning] = useState(false);  

    const handleChange=(e)=>    {
        setCount(e.target.value)

    }


    useEffect(() => {
        let interval;
        if (running) {

          interval = setInterval(() => {
            setCount((prevCount) => parseInt(prevCount )- 1);
            if(count <= 0){
                clearInterval(interval);
                beep();
                setRunning(false)  }


          }, 1000);

        } else if (!running) {
          clearInterval(interval);
        }
        return () => clearInterval(interval);
      }, [running]);




  return (
    <div>
        <h3> Cronometro para meditaci√≥n   <FaHourglass/> </h3>

        <div className='container-timer'>
        <input type='number' 
        onBlur={handleChange} ></input> <br></br>
        <button className='btn' onClick={()=>{setRunning(!running)}}></button><br></br>

        <div className='clock' >{count}</div>

        </div>

    </div>



  )
}

export default Timer
