import React , {useState, useEffect} from 'react'
import {FaHourglass} from 'react-icons/fa'


function Timer() {
    function beep() {
        var snd = new Audio("data:audio/wav;base64,");  
        snd.play();
    }

    const [count, setCount] =useState(Number(6))
    const [running, setRunning] = useState(false);  

    const handleChange=(e)=>    {
        setCount(e.target.value*60)

    }


    useEffect(() => {
        let interval;
        if (running) {

          interval = setInterval(() => {
            setCount((prevCount) => parseInt(prevCount )- 1);
            if(count <= 1){
                clearInterval(interval);                
                beep();
               setRunning(false)  }


          }, 1000);

        } else if (!running) {
          clearInterval(interval);
        }
        return () => clearInterval(interval);
      }, [running, count]);




  return (
    <div>
        <h3> Cronómetro para meditación   <FaHourglass/> </h3>
    <input type='number' placeholder='tiempo en minutos'
        onBlur={handleChange} ></input> <br></br>

        <div className='container-timer'>
        <input type='number'          ></input> <br></br>
        <button className='btn' onClick={()=>{setRunning(!running)}}>
          {count}</button><br></br>

        <div className='clock' >
          {count < 60? count :`${Math.floor(count/60)}:${count}`}
            </div>
        </div>

    </div>



  )
}

export default Timer
