        <div className='container-timer'>

        <button className='btn' onClick={()=>{setRunning(!running)}}>
          {!running? 'Activar': 'Detener'}</button><br></br>

        <div className='clock' >
          {count < 60? count :`${Math.floor(count/60)}:${count%60}`}

            </div>

        </div>

    </div>
    </div>


  )
}

export default Timer
